sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var street = replaceAll(current.street + '', "\r", "");
	street = replaceAll(street, "\n", " ");
	street = replaceAll(street, " ", "+");

	var city = replaceAll(current.city, " ", "+");
	var state = replaceAll(current.state, " ", "+");
	var zip = replaceAll(current.zip, " ", "+");
	var address = street + "," + city + "," + state + "," + zip;

	var request = new sn_ws.RESTMessageV2('TomTom API','Geocode Search');
	request.setStringParameter('query', address);
	var response = request.execute();	
	var responseBody = response.getBody();

	//current.u_comments = responseBody;
	//current.update();

	var data = JSON.parse(responseBody);
	current.latitude = data.results[0].position.lat;
	current.longitude = data.results[0].position.lon;	

	current.update();

})(current, previous);

function replaceAll(source, targetValue, replacementValue) {
	return source.split(targetValue).join(replacementValue);
}
