sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	// Set Request Type to Project if Work Type becomes Custom Project, and set description field message
	var work_type = g_form.getValue('work_type');

	if(work_type == 5 /* Custom Project */) {
		g_form.setValue('request_type', 9 /* Project */);
		g_form.showFieldMsg('description', 'NOTE: Custom Projects require full accurate description information be entered above.','warning');
	//	g_form.setMandatory('description', true);	
	}
	else {
		g_form.hideFieldMsg('description');
	//	g_form.setMandatory('description', false);	
	}

}
