sys_ui_action.script:
function GetDirections() {

	var urlBase = 'https://www.google.com/maps/dir//';

	var delivery_address_street = g_form.getValue('delivery_address_street');	
	var delivery_address_suite = g_form.getValue('delivery_address_suite');	
	var delivery_address_city = g_form.getValue('delivery_address_city');	
	var delivery_address_state_province = g_form.getValue('delivery_address_state_province');	
	var delivery_address_zip_postal_code = g_form.getValue('delivery_address_zip_postal_code');	

	var url = urlBase +  delivery_address_street + ' ' +  delivery_address_suite + ' ' + delivery_address_city 
					  + ' ' + delivery_address_state_province + ' ' + delivery_address_zip_postal_code;

	url = replaceAll(url,'/r','');  // Remove special characters
	url = replaceAll(url,'/n',' ');
	url = replaceAll(url,'#','');
	url = replaceAll(url,'  ',' '); // clean up extra spaces
	url = replaceAll(url,' ','+'); // replace spaces for + as expected by google

	window.open(url);	
}

function replaceAll(source, targetValue, replacementValue) {
	return source.split(targetValue).join(replacementValue);
}
