sys_ui_action.script:
// Conditional Access control: current incident state is not closed, user has role x_brb_work_order.user

current.update(); // Save any changes made to the incident form first, before proceeding, such as setting topic or other fields 1

// Check if WO Already Exists
var wo_check = new GlideRecord("x_brb_work_order_table");
wo_check.addQuery('parent',current.sys_id);
wo_check.query();

if(wo_check.next()) { /* If Work Order Already Exists For Incident*/

	// Stay on Incident and Show Message
	gs.addInfoMessage("Work Order " + wo_check.number + " already exists for Incident " + current.number + ". Review the Tabs at the bottom of the page." );
	action.setRedirectURL(current);
}
else { /* Else Work Order Does Not Yet Exists For This Incident */

	 /* Ready To Create */

	// Create Work Order and Set Fields
	var work_order = new GlideRecord("x_brb_work_order_table");
	work_order.initialize();

	// Set Parent as reference to Incident
	work_order.parent = current.sys_id;

	// set fields from INC
	// work_order.short_description = current.short_description;
	// work_order.description = current.description;

	work_order.local_contact = current.caller_id;	
	work_order.local_contact_phone = current.caller_id.phone;

	var user_id = gs.getUserID();
	var user = new GlideRecord('sys_user');
	user.get(user_id);
	work_order.technical_contact_phone = user.phone.toString();

	work_order.location = current.location;
	work_order.cmdb_ci = current.cmdb_ci;
	work_order.priority = current.priority;
	// work_order.sys_domain = current.sys_domain;

	// Save Work Order
	var sysID = work_order.insert();

	current.work_order_id = sysID;
	var mySysID = current.update();

	// add message of creation and redirect to work order
	gs.addInfoMessage("Work Order " + work_order.number + " created. Fill out all information and click Submit for Review button when ready");
	action.setRedirectURL(work_order);
	action.setReturnURL(current);

}
