// Set values for the task in this script.  Use the variable 'task' when setting additional values.
// Note: This script is run after the task values are set using the Fields, Template or Values you have specified.
//
// For example:
//     task.short_description = current.short_description;
task.parent = current.sys_id;
task.location = current.location;
task.assignment_group = current.assignment_group;
task.short_description = "Work order dispatch task: " + current.location.name;
task.description = current.description;
task.local_contact = current.local_contact;
task.opened_by = current.opened_by;
//task.expected_start = current.expected_start;
//task.u_expected_end = current.expected_start + current.estimated_work_duration;

task.work_order_task_type = 'Dispatch';
task.update();

workflow.info("Current dispatch task sysID is:" + task.sys_id);

// Send REST
var rest = new WorkOrderRestUtils();
var success = rest.sendDispatch( current.sys_id );

// Update Work Order State
if(success){
	current.state = 2; /* Work In Progress*/
	current.update();
}
else {
	current.state = -9; /* Error */
	current.update();
}

