function ifScript(){
	// If location, PC Profile or Approval group are Null set to default Approval Error Group
	if(current.location == ''|| current.location.x_brb_work_order_divisional_cio_grp == '')
		return 'no'; // Default Approval Error Override group
	// Else Glidescript for PC approval group
	else if(current.location.x_brb_work_order_divisional_cio_grp != '') {
		var grpMbr = new GlideRecord('sys_user_grmember');  
		grpMbr.addQuery('group', current.location.x_brb_work_order_divisional_cio_grp);  
		grpMbr.query();

		var count = grpMbr.getRowCount();
	// If Approval group has no members then set to default CRB	
		if(count == 0)
			return 'no'; // Default CRB group
		else
			workflow.scratchpad.cio_group = current.location.x_brb_work_order_divisional_cio_grp;
			return 'yes';
	} 
	workflow.info("Approval Group is: " + current.location.x_brb_work_order_divisional_cio_grp.name); //log approval group used in workflow
}

