// Resolve Parent Incident if in State of Active
// - Query Incident table for parent record to this WO, to check if parent is incident
var incident = new GlideRecord('incident');
incident.addQuery('sys_id', current.parent);
incident.query();

if(incident.next()) { /* If incident found */
	if(incident.state == 12 /* Pending Work Order */){ /* And In Pending Work Order State*/
		// Resolve the Incident, with Resolution Code Dispatch, and closure note
		incident.state = 6 /* Resolved */;  
		incident.close_notes = 'This record has been closed, as the work order attached has been closed';
		incident.u_choice_1 = 'Dispatch'; // Set Resolution Code To Dispatch
		incident.update();
		
		workflow.info('Resolved Pending Work Order Incident ' + incident.number);
	}
	else {
		// Log that No Action was taken because the incident wasn't in Pending Work Order state
		workflow.warn('Skipped Resolving Incident ' + incident.number + ' due to current state ' + incident.state);
	}
}
else {
	// Log that Parent was not a incident
	workflow.info('Parent not found as an incident (' + current.parent + ')' );
}

