// Set Parent Pending Work Order
// - Query Incident table for parent record to this WO, to check if parent is incident
var parentTask = new GlideRecord('task');
parentTask.addQuery('sys_id', current.parent);
parentTask.query();

if(parentTask.next()) { /* If Parent found */
	
	// Set Incident to Pending Work Order
	parentTask.state = 12 /* Pending Work Order */;  
	parentTask.work_notes = 'A new work order has been created to resolve the issue';
	parentTask.update();
	
	workflow.info('Parent set to Pending Work Order state ' + partask.number);
}
else {
	// Log that Parent was not a incident
	workflow.info('Parent not found as a task (' + current.parent + ')' );
}

